import React from 'react';
import {parseToYearAndMonth} from '../utility'
import {Image} from 'antd'
import {withRouter} from "react-router-dom";


class ImageDemo extends React.Component {
    constructor(props) {
        super(props);
    };

    selectedItem = (item) => {
        this.setState({selectedItem:item})
    }
    // getIntoDetail=()=>{
    //     this.props.history.push('/Detail')
    // }


    render() {
        const {items,onSelectedItems2, category} = this.props
        const parsedDate = parseToYearAndMonth(items.date)

        return (
            <React.Fragment>
                <div className="container">
                    <div className="row" style={{textAlign: "center",}}>
                        {
                            items.map((item) => {

                                const id = item._uid

                                return (
                                    <div
                                        className="col-xs-6 col-sm-3"
                                        style={{width: 245, overflow: "hidden"}}
                                        onClick={(event)=>{{
                                            this.props.history.push(`/${category}/${id}`)
                                            onSelectedItems2(item, category)
                                        }}}
                                    >
                                        <img
                                            width={245}
                                            src={item.image}
                                            placeholder={
                                                <img
                                                    src={`${item.image}?x-oss-process=image/quality,q_1/resize,w_200`}
                                                    width={200}
                                                />
                                            }
                                        />
                                        <p>{item.ig_handle}</p>
                                        <p>{new Date(item.date).toDateString().split(" ")[1]} {new Date(item.date).toDateString().split(" ")[3]}</p>
                                    </div>
                                )
                                }
                            )}
                    </div>
                </div>
            </React.Fragment>
        );
    }
}


export default withRouter(ImageDemo)
