import React, {Component} from 'react';
import {withRouter} from 'react-router-dom'
import axios from "axios";
import {convertResponseData} from "../../App";


function getContentById(id, data) {
    for (let i = 0; i < data.length; i++) {
        if (data[i].id == id) {
            return data[i];
        }
    }
}

class StorageDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: ''
        }
    }

    async componentDidMount() {

        console.log('this.props', this.props);
        const {id} = this.props.match.params
        console.log('id', id);
        this.setState({
            id
        })
        const resData = await axios.get('/story')
        let {storage} = convertResponseData(resData)
        console.log('storage', storage);
        getContentById(id,storage)
        console.log(getContentById(id,storage))

    }


    render() {
        const {DetailOfItem} = this.props
        return (
            <React.Fragment>
                <div>
                    {/*<div>{DetailOfItem.date}</div>*/}
                    {/*<div>{DetailOfItem.content}</div>*/}
                    {/*<div>{DetailOfItem.ig_handle}</div>*/}
                    {this.state.id}
                </div>
            </React.Fragment>
        );
    }
}

export default withRouter(StorageDetail)
